	// Calculate CRC for PDU send to PowerMax
	calcCRC: function(pdu) {
		var checksum = 0;
		var len = pdu.length;

		if (len <= 2) { return false }
		for (var i = 1; i <= (len - 3); i++) {
			checksum += pdu[i];
		}
		checksum = 0xFF - (checksum % 0xFF);
		if (checksum == 0xFF) { checksum = 0x00; }
		var newPDU = pdu.slice(0, len - 2);
		newPDU.push(checksum, 0x0A);
		return newPDU
	},

	// Check CRC in PDU received from PowerMax
	checkCRC: function(pdu) {
		var checksum = 0;
		var len = pdu.length;

		if (len <= 2) { return false }
		for (var i = 1; i <= (len - 3); i++) {
			checksum += pdu[i];
		}
		checksum = 0xFF - (checksum % 0xFF);
		if (checksum == 0xFF) { checksum = 0x00; }
		var got = pdu[len - 2];
		if (checksum == got) {
			return true
		} else {
			//'CRC check failed (expected ' + checksum + ', got ' + got + ')';
			return false;
		}
	},

